mapper.insert:
  	
	SELECT SEQ_IPO_PICKUP.NEXTVAL FROM dual
	
    insert into IPO_PICKUP (PICKUP_ID, PICKUP_PASSWORD, IDCARD_NUM
      )
    values (#{pickupId,jdbcType=VARCHAR}, #{pickupPassword,jdbcType=VARCHAR}, #{idcardNum,jdbcType=VARCHAR}
      )

mapper.update:
update IPO_PICKUP
set PICKUP_PASSWORD = #{pickupPassword,jdbcType=VARCHAR},
  IDCARD_NUM = #{idcardNum,jdbcType=VARCHAR}
where PICKUP_ID = #{pickupId,jdbcType=VARCHAR}

mapper.select:
select PICKUP_ID, PICKUP_PASSWORD, IDCARD_NUM
from IPO_PICKUP
where PICKUP_ID = #{pickupId,jdbcType=VARCHAR}

  	select p.*,t.* from ipo_deliveryorder d left join ipo_delivery_cost p on d.DELIVERYORDER_ID = p.delivery_id 
	left join ipo_pickup t on t.pickup_id = d.method_id where d.deliveryorderId = #{deliveryorderId,jdbcType=VARCHAR} 
	and d.method_id = #{methodid,jdbcType=VARCHAR}
