mapper.select:
  select * from (
    select ,rownum TNO from IPO_SPO_COMMODITYMANMAAGEMENT
    where 1=1
    
	and COMMUNITY_ID = #{Spo.communityId,jdbcType=VARCHAR}  
    
    
    and RATION_TYPE = #{Spo.rationType,jdbcType=VARCHAR}
    
    
    and RATION_SATE = #{Spo.rationSate,jdbcType=DECIMAL}
    
    
    and REGISTER_DATE = #{Spo.registerDate,jdbcType=DATE}
    
    
    and SPO_DATE = #{Spo.spoDate,jdbcType=DATE}
    
    
    and IPO_DATE = #{Spo.ipoDate,jdbcType=DATE}
    
    ) a where a.TNO between  #{beginnum} and  #{endnum} order by a.spo_id

mapper.update:
update IPO_SPO_COMMODITYMANMAAGEMENT
 set RATION_SATE = #{rationSate,jdbcType=DECIMAL}
 where SPO_ID = #{spoid,jdbcType=VARCHAR}
