mapper.select:
       select * from (
	        select tc.id,tc.plan,tc.commodity_id,conf.commodityname,conf.price,conf.counts,rownum as row_num  
			from IPO_TRUSTEESHIP_COMMODITY tc
			left join ipo_commodity_conf conf on (tc.commodity_id=conf.commodityid)
			where tc.delete_flag=0 
			
				and tc.commodity_id=#{commodity.commodityId} 
			
			and #{endIndex} >=rownum
		) where row_num>=#{startIndex}

mapper.insert:
	    
		select seq_ipo_trusteeship.nextval from dual
		
       insert into ipo_trusteeship(
          id, apply_amount,trusteeship_commodity_id,state, price,   
	      create_user, create_date, commodity_id
 	   )values (
 	      #{id,jdbcType=INTEGER}, #{applyAmount},#{trusteeshipCommodityId}, #{state}, #{price},  
          #{createUser},#{createDate,jdbcType=TIMESTAMP}, #{commodityId}
       )

mapper.select:
select * from ( select ship.id,ship.commodity_id,comm.commodityname,cu.name as create_user_name,ship.apply_amount,ship.instorage_amount, trucomm.plan,ship.delay_charge,ship.state,ship.create_date,ship.auditing_date,biware.warehousename,rownum as row_num from ipo_trusteeship ship left join ipo_commodity_sale comm on (ship.commodity_id=comm.commodityid) left join ipo_trusteeship_commodity trucomm on (ship.trusteeship_commodity_id=trucomm.id) left join ipo_trusteeship_warehouse truware on (ship.commodity_id=truware.commodity_id) left join bi_warehouse biware on (truware.warehouse_id=biware.id) left join C_USER cu on (ship.create_user=cu.id) where ship.delete_flag=0
and ship.commodity_id=#{ship.commodityId}
and comm.commodityname like '%'||#{ship.commodityName}||'%'
and ship.state=#{ship.state}
and truware.warehouse_id=#{ship.warehouseId}
and ship.create_date>=to_date(#{ship.beginCreateDate},'mm/dd/yyyy')
and to_date(#{ship.endCreateDate},'mm/dd/yyyy')>=ship.create_date
and ship.auditing_date>=to_date(#{ship.beginAuditingDate},'mm/dd/yyyy')
and to_date(#{ship.endAuditingDate},'mm/dd/yyyy')>= ship.auditing_date
and #{endIndex} >=rownum ) where row_num>=#{startIndex}

select count(ship.id ) as count_ from ipo_trusteeship ship left join ipo_commodity_sale comm on (ship.commodity_id=comm.commodityid) left join ipo_trusteeship_commodity trucomm on (ship.trusteeship_commodity_id=trucomm.id) left join ipo_trusteeship_warehouse truware on (ship.commodity_id=truware.commodity_id) left join bi_warehouse biware on (truware.warehouse_id=biware.id) left join C_USER cu on (ship.create_user=cu.id) where ship.delete_flag=0
and ship.commodity_id=#{ship.commodityId}
and comm.commodityname like '%'||#{ship.commodityName}||'%'
and ship.state=#{ship.state}
and truware.warehouse_id=#{ship.warehouseId}
and ship.create_date>=to_date(#{ship.beginCreateDate},'mm/dd/yyyy')
and to_date(#{ship.endCreateDate},'mm/dd/yyyy')>= ship.create_date
and ship.auditing_date>=to_date(#{ship.beginAuditingDate},'mm/dd/yyyy')
and to_date(#{ship.endAuditingDate},'mm/dd/yyyy')>=ship.auditing_date

mapper.update:
update ipo_trusteeship t set t.state=#{ship.state},t.update_user=#{ship.updateUser},
       t.update_date=#{ship.updateDate,jdbcType=TIMESTAMP}
where t.id=#{ship.id}
