mapper.select:
select SUBSCRIBEID, UNDERWRITERID, COMMODITYID, SUBSCRIBECOUNTS, PROPORTION, DELETE_FLAG, 
CREATE_USER, CREATE_DATE, UPDATE_USER, UPDATE_DATE,SUBSCRIBEPRICE,STATE
from IPO_UNDERWRITER_SUBSCRIBE where DELETE_FLAG=0  and  UNDERWRITERID=#{brokerid,jdbcType=VARCHAR} and COMMODITYID=#{commodityid,jdbcType=VARCHAR}

select SUBSCRIBEID, UNDERWRITERID, COMMODITYID, SUBSCRIBECOUNTS, PROPORTION, DELETE_FLAG, 
CREATE_USER, CREATE_DATE, UPDATE_USER, UPDATE_DATE,SUBSCRIBEPRICE,STATE
from IPO_UNDERWRITER_SUBSCRIBE where 1=1 
 
 and   UNDERWRITERID = #{record.underwriterid,jdbcType=VARCHAR} 
 
 
 and   COMMODITYID = #{record.commodityid,jdbcType=VARCHAR} 
 
  
 and   DELETE_FLAG = #{record.deleteFlag,jdbcType=DECIMAL}

SELECT A.* FROM( SELECT T.*,ROWNUM TNO FROM(SELECT C.PUBMEMBERID,S.COMMODITYID,C.COMMODITYNAME,S.UNDERWRITERID,B.NAME FROM IPO_UNDERWRITER_SUBSCRIBE S LEFT JOIN IPO_COMMODITY_CONF C ON S.COMMODITYID=C.COMMODITYID LEFT JOIN BR_BROKER B ON S.UNDERWRITERID = B.BROKERID WHERE S.DELETE_FLAG=0
and C.PUBMEMBERID like concat (#{record.pubmemberid,jdbcType=VARCHAR},'%')
ORDER BY C.PUBMEMBERID)T) A WHERE A.TNO BETWEEN #{beginnum} AND #{endnum}

SELECT COUNT(*) FROM(SELECT C.PUBMEMBERID,S.COMMODITYID,C.COMMODITYNAME,S.UNDERWRITERID,B.NAME FROM IPO_UNDERWRITER_SUBSCRIBE S LEFT JOIN IPO_COMMODITY_CONF C ON S.COMMODITYID=C.COMMODITYID LEFT JOIN BR_BROKER B ON S.UNDERWRITERID = B.BROKERID WHERE S.DELETE_FLAG=0
and C.PUBMEMBERID like concat (#{record.pubmemberid,jdbcType=VARCHAR},'%')
)
